(109, 312, 'Worship', 'worship', '<p>.</p>', '', 1, 94, '2019-08-03 05:18:53', 42, '', '2019-08-03 11:02:07', 42, 0, '0000-00-00 00:00:00', '2019-08-03 05:18:53', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 3, 8, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '', ''),
(110, 313, 'Liturgy ', 'liturgy', '<p style="text-align: justify;">Some might be tempted to think of liturgy as simply meaning "ritual" or ceremony. However, while liturgy does include ritual it is something much deeper than mere ritualism or ceremonial, and it is more than just the communal expression of our personal feelings of devotion.</p>\r\n<p style="text-align: justify;">Liturgy is not just a matter of taste, or churchmanship. Liturgy is central to Christianity and is an integral part of the Christian family''s relationship with God. The signs, symbols and sacred actions which form public prayer and worship spring from the language and events of God''s own self-revelation. Anglicans'' liturgical celebrations arise directly out of the mystery of salvation in Jesus Christ and are part of the very means by which they enter into that mystery.</p>', '', 1, 94, '2019-08-03 05:19:20', 42, '', '2019-08-05 08:42:15', 42, 0, '0000-00-00 00:00:00', '2019-08-03 05:19:20', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 2, 7, '', '', 1, 0, '{